// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

interface IERC20 {
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
    function decimals() external view returns (uint8);
}

contract PiggyJarUSDC {
    event Deposited(address indexed from, uint256 amount);
    event Withdrawn(address indexed to, uint256 amount);
    event Broken(address indexed to, uint256 amount);

    address public immutable owner;
    IERC20 public immutable token; // USDC or similar
    string public name; // jar name

    modifier onlyOwner() {
        require(msg.sender == owner, "not owner");
        _;
    }

    constructor(address _owner, address _token, string memory _name) {
        require(_owner != address(0), "owner");
        require(_token != address(0), "token");
        owner = _owner;
        token = IERC20(_token);
        name = _name;
    }

    function deposit(uint256 amount) external onlyOwner {
        require(amount > 0, "amount");
        bool ok = token.transferFrom(msg.sender, address(this), amount);
        require(ok, "transferFrom");
        emit Deposited(msg.sender, amount);
    }

    function withdraw(uint256 amount) external onlyOwner {
        require(amount > 0, "amount");
        bool ok = token.transfer(owner, amount);
        require(ok, "transfer");
        emit Withdrawn(owner, amount);
    }

    function breakJar() external onlyOwner {
        uint256 bal = token.balanceOf(address(this));
        bool ok = token.transfer(owner, bal);
        require(ok, "transfer");
        emit Broken(owner, bal);
    }

    function balance() external view returns (uint256) {
        return token.balanceOf(address(this));
    }
}
