// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {Clones} from "@openzeppelin/contracts/proxy/Clones.sol";

interface IPiggyJar {
    function initialize(address _owner, uint256 _targetUsd, uint256 _thresholdUsd, string memory _name) external;
}

contract PiggyJarFactory {
    event JarCreated(address indexed owner, address jar, uint256 targetUsd, uint256 thresholdUsd, bytes32 nameHash);

    address public immutable implementation;

    constructor(address _implementation) {
        implementation = _implementation;
    }

    function createJar(uint256 targetUsd, uint256 thresholdUsd, string calldata name) external returns (address jar) {
        jar = Clones.clone(implementation);
        IPiggyJar(jar).initialize(msg.sender, targetUsd, thresholdUsd, name);
        emit JarCreated(msg.sender, jar, targetUsd, thresholdUsd, keccak256(bytes(name)));
    }
}
